void DrawGridLines()
{
    // glLineWidth(1.0f);

    glEnable(GL_BLEND);
    glBlendFunc(GL_SRC_ALPHA, GL_ONE_MINUS_SRC_ALPHA);

    glBegin(GL_LINES);

    glColor4f(1.0f, 1.0f, 1.0f, 0.5f);

    // vertical lines
    for (float x = -1.0f; x <= 1.0f; x = x + 0.01f)
    {
        glVertex2f(x, 1.0f);
        glVertex2f(x, -1.0f);
    }

    // horizontal lines
    for (float y = -1.0f; y <= 1.0f; y = y + 0.01f)
    {
        glVertex2f(1.0f, y);
        glVertex2f(-1.0f, y);
    }

    glColor4f(1.0f, 1.0f, 1.0f, 1.0f);
    // vertical lines
    for (float x = -1.0f; x <= 1.0f; x = x + 0.1f)
    {
        glVertex2f(x, 1.0f);
        glVertex2f(x, -1.0f);
    }

    // horizontal lines
    for (float y = -1.0f; y <= 1.0f; y = y + 0.1f)
    {
        glVertex2f(1.0f, y);
        glVertex2f(-1.0f, y);
    }

    // changing color of middle lines for understanding
    // horizontal
    glColor4f(1.0f, 0.0f, 0.0f, 1.0f);

    glVertex2f(-1.0f, 0.0f);
    glVertex2f(1.0f, 0.0f);

    // vertical
    glVertex2f(0.0f, -1.0f);
    glVertex2f(0.0f, 1.0f);

    glEnd();
}

